package practice;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10 ,b=20,c;
		c=a+b;
		System.out.println(c);
		a+=3;
		System.out.println(a);
		//relation opertor
		int d=10,e=20;
		char s='s',y='s';
		String x="san",z="san";
		System.out.println(d>e);
		System.out.println(s==y);
		System.out.println(x==z);
		System.out.println(s==y);
	}

}
